    header .header-vertical-menu {
        background: #f37100;
        border-radius: 16px;
        white-space: nowrap;
        position: relative;
        transition: all 0.5s;
        box-shadow: 0px 0px 0px 4px rgb(242 114 0 / 31%);
        margin-right: 70px;
    }

    header .header-vertical-menu .header-vertical-menu__opener {
        position: relative;
        display: flex;
        align-items: center;
        background: linear-gradient(180deg, #ed7400 0%, #f64600 100%);
        border-radius: 16px;
        padding: 0 16px;
        min-height: 48px;
        transition: all 0.1s;
    }

    header .header-vertical-menu .header-vertical-menu__opener:before {
        content: "";
        position: absolute;
        display: block;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        transition: all 0.1s;
        border: 3px solid #ffad80;
        box-shadow: 0px 4px 8px rgb(240 115 0 / 33%);
        border-radius: 16px;
        box-sizing: border-box;
        pointer-events: none;
        height: 100%;
        width: 100%;
    }

